import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import "../stylesheets/App.css";

const AddTransaction = ({ addTransactionFun }) => {
  const [date, setDate] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [amount, setAmount] = useState("");

  // State variable to track if the button should be disabled
  const [isButtonDisabled, setIsButtonDisabled] = useState(true);

  // Function to handle changes in the input fields
  const handleChange = (evt) => {
    const { name, value } = evt.target;
    switch (name) {
      case "date":
        setDate(value);
        break;
      case "description":
        setDescription(value);
        break;
      case "category":
        setCategory(value);
        break;
      case "amount":
        setAmount(value);
        break;
      default:
        break;
    }
  };

  // Function to handle form submission
  const handleSubmit = (evt) => {
    evt.preventDefault();
    // Send the new transaction data to the server
    const newTransaction = {
      date,
      description,
      category,
      amount: parseFloat(amount),
    };
    addTransactionFun(newTransaction);
    // Reset the input fields after successful addition
    setDate("");
    setDescription("");
    setCategory("");
    setAmount("");
  };

  // Function to check if any input field is empty and update the button disabled state
  useEffect(() => {
    if (date.trim() === "" || description.trim() === "" || category.trim() === "" || amount.trim() === "") {
      setIsButtonDisabled(true);
    } else {
      setIsButtonDisabled(false);
    }
  }, [date, description, category, amount]);

  return (
    <div className="segment">
      <form className="form" onSubmit={handleSubmit}>
        <div className="inline fields">
          <input
            type="date"
            name="date"
            value={date}
            onChange={handleChange}
          />
          <input
            type="text"
            name="description"
            placeholder="Description"
            value={description}
            onChange={handleChange}
          />
          <input
            type="text"
            name="category"
            placeholder="Category"
            value={category}
            onChange={handleChange}
          />
          <input
            type="number"
            name="amount"
            placeholder="Amount"
            step="0.01"
            value={amount}
            onChange={handleChange}
          />
        </div>
        <button className="primary-btn" type="submit" disabled={isButtonDisabled}>
          Add Transaction
        </button>
      </form>
    </div>
  );
};

AddTransaction.propTypes = {
  addTransactionFun: PropTypes.func.isRequired,
};

export default AddTransaction;
